import React from 'react' ;
import {View, Text, Button, ScrollView, Modal, StyleSheet, Image } from 'react-native';



const SModal = props => {

const styles = StyleSheet.create({
  content:{
    alignItems: 'center',
    flex: 1,
    justifyContent: 'center',

  },

  edittext:{
   padding: 10,
    fontfamily:  'Adobe Garamond',
    fontSize: 15,
    marginTop: 5,
  },

    btnDesign: {
    justifyContent: 'center',
    alignItems: 'center',
    padding: 10,
    marginTop: 5,
    fontSize: 15,
  },

});

  return(
    
      <Modal visible={props.visible} animationType="fade">
      <ScrollView>
         <View style={styles.content}>
          
          <Image style={{width: 200, height: 200}} source={{uri:'https://media.tenor.com/images/0283f8169ce766913c3bb8c615dd6ec7/tenor.gif'}} />

<Text style={styles.edittext}>.{'\n\n'}

.{'\n\n'}

.{'\n\n'}

.{'\n\n'}

</Text>
          </View>
        </ScrollView>
        <View style={styles.btnDesign}>
        <Button title="Close" onPress={props.onClose} />
        </View>
      </Modal>

   
  );
};

export default SModal;
